import { useContext, useEffect, useRef } from "react";
import { useLocation } from "react-router-dom";
import { ScrollContext } from "./context/ScrollProvider";

const PageTracker = (params) => {
  const location = useLocation();
  const previousLocation = useRef(location.pathname);
  const {scroll, SetScroll} = useContext(ScrollContext)

  useEffect(() => {
    console.log(String(location.pathname) , params.pages, String(location.pathname) in params.pages);
    if (previousLocation.current !== location.pathname && location.pathname in params.pages) {
        console.log("UJSIDFH")
        window.scrollTo(0, scroll);
        SetScroll(0);
    }
    previousLocation.current = location.pathname;
  }, [location]);

  return null; 
};

export default PageTracker;
