import { useContext, useEffect, useRef } from "react";
import { useLocation } from "react-router-dom";
import { ScrollContext } from "./context/ScrollProvider";

const PageTracker = (params) => {
  const location = useLocation();
  const previousLocation = useRef(location.pathname);
  const {scroll, setScroll} = useContext(ScrollContext)

  useEffect(() => {
    if (previousLocation.current !== location.pathname) {
        if (params.pages.includes(location.pathname))
        {
            setTimeout(() => {window.scrollTo(0, scroll)}, 100);
        }
        if (params.pages.includes(previousLocation.current))
        {
            setScroll(window.scrollY);
        }
    }
    previousLocation.current = location.pathname;
  }, [location]);

  return null; 
};

export default PageTracker;
