import { useContext, useEffect, useState } from "react";
import { AuthContext } from "./context/AuthProvider";
import "./styles/Account.css"
import { useParams } from "react-router-dom";
import axios from "axios";
import { useNavigate } from "react-router-dom";
import FeedPost from "./FeedPost";
import { v4 as uuidv4 } from 'uuid';

const AccountComponent = (params) => {
    const { username } = useParams();
    const {auth, setAuth} = useContext(AuthContext);
    const [userData, setUserData] = useState({});
    const [dataLoadStatus, setLoadStatus] = useState("pending");
    const navigate = useNavigate();

    useEffect(
        () => {
            const fetchData = async () => {
                try
                {
                    console.log(params.api_url + "users_detail/?username=" + (username ? username : auth?.user["username"]))
                    const responce = await axios.get(params.api_url + "users_detail/?username=" + (username ? username : auth?.user["username"]));
                    console.log(responce.data);
                    setUserData(responce.data);
                    setLoadStatus("success");
                    return responce.data;
                }
                catch (e)
                {
                    setLoadStatus("fail");
                    console.log(e);
                }

            }
            fetchData();
        }
        , [username]
    )    
    if (username)
    {
        return (
            <>
                <div className="user-profile-info-box">
                    <div className="user-username">  {username} </div>
                    <img className="user-profile-image" src={ params.images_path + userData["user_extra_data"]["profile_pic"] }></img>
                </div>
                <div className="user-username" styles={(text-align: center)}> Посты пользователя </div>
                {
                        dataLoadStatus == "success" ?
                        (
                        <div className="user-posts-box">
                            {
                                userData["posts"].map((item) => (
                                    <FeedPost item={item} key={uuidv4()}></FeedPost> // здесь они не будут перерисовываться но дублироваться могут как вариант решил использовать рандомные идентификаторы
                                ))
                            }
                        </div>
                         ) : dataLoadStatus == "pending" ? (<div> Loading user posts... </div>) :
                         (<div> Failed to load user posts </div>)
                }
            </>
        )
    }
    if (auth?.user["username"] == username)
    {
        navigate("/account");
    }
    if (auth?.user["username"])
        {
            return (
                <>
                    <h1 className="user-username" >{auth.user["username"]}</h1>
                    <h2 className= "user-email">{auth.user["email"]}</h2>
                    {
                        dataLoadStatus == "success" ?
                        (
                        <div className="user-posts-box">
                            {
                                userData["posts"].map((item) => (
                                    <FeedPost item={item} key={uuidv4()}></FeedPost> // здесь они не будут перерисовываться но дублироваться могут как вариант решил использовать рандомные идентификаторы
                                ))
                            }
                        </div>
                         ) : dataLoadStatus == "pending" ? (<div> Loading user posts... </div>) :
                         (<div> Failed to load user posts </div>)
                    }
                </>
            );
        }
        else
        {
            return ( 
                <div> Пользователь не зарегистрирован</div>
            );
        }
}

export default AccountComponent;
