import { useContext, useEffect, useState } from "react";
import { AuthContext } from "./context/AuthProvider";
import "./styles/Account.css";
import { useParams } from "react-router-dom";
import axios from "axios";
import { useNavigate } from "react-router-dom";
import FeedPost from "./FeedPost";
import { v4 as uuidv4 } from "uuid";
import DeletePopup from "./DeletePopup";

const AccountComponent = (props) => {
  const { username } = useParams();
  const { auth, setAuth } = useContext(AuthContext);
  const [userData, setUserData] = useState({});
  const [dataLoadStatus, setLoadStatus] = useState("pending");
  const [editingBio, setEditingBioStatus] = useState(false);
  const [newBioText, setNewBioText] = useState("");
  const [deletingAccount, setDeletingAccountState] = useState(false);
  const navigate = useNavigate();

  function exitAccount() {
    localStorage.setItem("authToken", "");
    setAuth({});
    navigate("/");
  }

  const deleteAccount = async () => {
    try {
      const responce = await axios.delete(
        props.api_url +
          "users_detail/?username=" +
          (username ? username : auth?.user["username"])
      );
      if (responce.status == 200) {
        localStorage.setItem("authToken", "");
        setAuth({});
        navigate("/");
      }
    } catch (e) {
      console.log(e);
    }
  };

  const updateProfile = async (newProfileData) => {
    try {
      const formData = new FormData();
      formData.append(`user_origin`, newProfileData["user_origin"]);
      if (newProfileData["bio"]) {
        formData.append(`bio`, newProfileData["bio"]);
      }
      if (newProfileData["profile_pic"]) {
        formData.append(`profile_pic`, newProfileData["profile_pic"]);
      }
      const responce = await axios.put(
        props.api_url + "update_user_data",
        formData
      );
      setLoadStatus("success");
      navigate("/account/" + auth?.user["username"]);
      return responce.data;
    } catch (e) {
      setLoadStatus("fail");
      console.log(e);
    }
  };

  function changeProfilePic(picture) {
    const new_profile_data = {
      user_origin: auth?.user["id"],
      profile_pic: picture,
    };
    updateProfile(new_profile_data);
  }

  const changeBio = async () => {
    setEditingBioStatus(false);

    if (newBioText != userData["user_extra_data"]["bio"]) {
      const new_profile_data = {
        user_origin: auth?.user["id"],
        bio: newBioText,
      };
      updateProfile(new_profile_data);
    }
  };

  useEffect(() => {
    const fetchData = async () => {
      try {
        const responce = await axios.get(
          props.api_url +
            "users_detail/?username=" +
            (username ? username : auth?.user["username"])
        );
        if (responce.data) {
          setUserData(responce.data);
          setNewBioText(responce?.data["user_extra_data"]["bio"]);
          setLoadStatus("success");
          return responce.data;
        }
      } catch (e) {
        setLoadStatus("fail");
        console.log(e);
      }
    };
    if (auth?.user?.username || username) {
      fetchData();
    } else {
      setLoadStatus("fail");
    }
  }, [username]);

  if (auth?.user?.username === username && username) {
    navigate("/account");
  }

  return (
    <>
      {dataLoadStatus == "success" ? (
        <>
          <DeletePopup
            text={"Вы уверены, что хотите удалить свой аккаунт?"}
            dependentProp={deletingAccount}
            setDependentProp={setDeletingAccountState}
            Action={deleteAccount}
          />
          <div className="user-profile-info-box">
            <div className="user-username">
              {" "}
              {userData["user_info"]["username"]}{" "}
            </div>
            {username ? null : (
              <div className="user-email">
                {" "}
                {"Email: " + userData["user_info"]["email"]}{" "}
              </div>
            )}
            <div className="user-image-bio-box">
              {userData["user_extra_data"]["profile_pic"] ? (
                <img
                  className="user-profile-image"
                  src={
                    props.images_path +
                    userData["user_extra_data"]["profile_pic"]
                  }
                ></img>
              ) : null}
              {editingBio ? (
                <textarea
                  className="user-bio-textarea"
                  onChange={(e) => {
                    setNewBioText(e.target.value);
                  }}
                  value={newBioText}
                ></textarea>
              ) : userData["user_extra_data"]["bio"] != "" ? (
                <div className="user-bio-text">
                  {userData["user_extra_data"]["bio"]}
                </div>
              ) : null}
            </div>
            {username ? null : (
              <div className="profile-control-box">
                <div className="change-profile-picture-box">
                  <label>Сменить изображение профиля</label>
                  <br />
                  <input
                    accept="image/*"
                    type="file"
                    multiple
                    onChange={(e) => changeProfilePic(e.target.files[0])}
                    className="change-profile-picture"
                  ></input>
                </div>
                {editingBio ? (
                  <button onClick={changeBio}>
                    {" "}
                    Закончить редактирование биографии
                  </button>
                ) : (
                  <button
                    onClick={() => {
                      setEditingBioStatus(true);
                    }}
                    className="change-bio"
                  >
                    {" "}
                    Сменить биографию{" "}
                  </button>
                )}
                <button onClick={exitAccount} className="exit-button">
                  {" "}
                  Выйти{" "}
                </button>
                <button
                  onClick={(e) => {
                    setDeletingAccountState(true);
                  }}
                  className="delete-account-button"
                >
                  Удалить аккаунт
                </button>
              </div>
            )}
            <br />
            <br />
            <div className="user-posts-text"> Посты пользователя </div>
          </div>
          <div className="user-posts-box">
            {userData["posts"] && userData["posts"].length > 0 ? (
              userData["posts"].map((item) => (
                <FeedPost item={item} key={uuidv4()}></FeedPost>
              ))
            ) : (
              <div className="user-no-posts-text">
                Пользователь ничего не запостил (пока)
              </div>
            )}
          </div>
        </>
      ) : dataLoadStatus == "pending" ? (
        <div>
          <br />
          <br />
          <br />
          <br /> <h2>Loading user data....</h2>{" "}
        </div>
      ) : (
        <div>
          <br />
          <br />
          <br />
          <br /> <h2>Failed to load user data</h2>{" "}
        </div>
      )}
    </>
  );
};

export default AccountComponent;
