import { AuthContext } from "./context/AuthProvider";
import { useContext } from "react";
import useLoad from "./UseLoad";
import "./styles/PostDetail.css"

// class post_serializer(serializers.ModelSerializer):
//     class Meta:
//         model = Post
//         fields = ["id", "author", "postname", "content", "created_at"]

const PostComponent = (props) => {
    const {auth, setAuth} = useContext(AuthContext);

    const { post_id } = useParams();

    const [data, receivedData] = useLoad(props.fetch_post_url + "?request_id=" + post_id);

    return ( 
        <>
            <h1 className="post-name">{data["post_info"]["postname"]}</h1>
            <div className="post-content">{data["post_info"]["content"]}</div>
            <div className="post-images-box">
                {
                    data["images"].map((item) => {
                        <img className="post-image" src={ props.image_path + item["file"] }></img>
                    })
                }
            </div>
        </>
     );
}

export default PostComponent;
