import { useContext, useState } from "react";
import "./styles/NewPost.css";
import axios from "axios";
import { AuthContext } from "./context/AuthProvider";

import AddImagesComponent from "./AddImagesComponent";

const NewPostComponent = (props) => {
  const { auth, setAuth } = useContext(AuthContext);

  const [postName, setPostName] = useState("");
  const [postContent, setPostContent] = useState("");
  const [images, setImages] = useState([]);
  const [sentDataToServer, setDataSendStatus] = useState("");

  function PostNewPost(event) {
    const postData = async () => {
      const formData = new FormData();

      // Добавляем данные поста
      formData.append("author", auth["user"]["id"]);
      formData.append("postname", postName);
      formData.append("content", postContent);

      // Добавляем изображения
      images.forEach((image, index) => {
        formData.append(`image_${index}`, image.file);
      });

      try {
        const response = await axios.post(
          props.api_url + "new_post",
          formData,
          {
            headers: {
              "Content-Type": "multipart/form-data",
            },
          }
        );
        setImages([]);
        setPostName("");
        setPostContent("");
        setDataSendStatus("success");
      } catch (error) {
        console.error("Ошибка при отправке:", error);
        setDataSendStatus("fail");
      }
    };
    event.preventDefault();
    try {
      if (auth?.user) {
        postData();
      }
    } catch (e) {
      console.log(e);
      setDataSendStatus("fail");
    }
    return 0;
  }

  return (
    <>
      <div className="new-post-box">
        <form className="new-post-form" onSubmit={PostNewPost}>
          <label htmlFor="PostNameInput"> Название: </label>
          <input
            type="text"
            id="PostNameInput"
            placeholder="введите название поста"
            onChange={(e) => {
              setPostName(e.target.value);
            }}
            value={postName}
            required
          />
          <label htmlFor="PostContentInput"> Текст: </label>
          <textarea
            type="text"
            id="PostContentInput"
            placeholder="о чем расскажете?"
            onChange={(e) => {
              setPostContent(e.target.value);
            }}
            value={postContent}
            required
          ></textarea>
          <br />
          <AddImagesComponent
            max_images={10}
            images={images}
            setImages={setImages}
          ></AddImagesComponent>
          <br />
          <br />
          <br />
          <button>Запостить</button>
        </form>
      </div>
    </>
  );
};

export default NewPostComponent;
