import useLoad from "./UseLoad";
import FeedPost from "./FeedPost";
import "./styles/FeedContent.css"
import { AuthContext } from "./context/AuthProvider";
import { useContext, useEffect, useState } from "react";

const FeedContent = (props) => {

    const [lastPost, SetLastPost] = useState("")

    let auth = useContext(AuthContext);

    console.log(props.api_url + lastPost)

    const {data: feedData, receivedData: got_data} = useLoad(props.api_url + lastPost);



    if (got_data == "Pending") {
        return (
            <>
                <div className="loading-block">
                    <h2> Loading data... </h2>
                </div>
            </>
        );
    }

    if (got_data == "Fail")
    {
        return (
            <>
                <div className="loading-block">
                    <h2> Failed to fetch data </h2>
                </div>
            </>
        );
    }

    useEffect(
        () => {
            if (feedData.length > 0)
                {
                    SetLastPost(feedData[feedData.length-1].created_at);
                }
            }
    , [feedData])

    return ( 
        <>
            <div className="feed-content"> 
                <div className="feed-box">
                    {
                        feedData.map((item) => (
                            <FeedPost item={item} key={item.id}></FeedPost>
                        ))
                    }
                </div>
            </div>
        </>
     );
}

export default FeedContent;
