import FeedPost from "./FeedPost";
import "./styles/FeedContent.css"
import { AuthContext } from "./context/AuthProvider";
import { useContext, useEffect, useState } from "react";
import axios from 'axios'

const FeedContent = (props) => {

    const [hasScrolledToBottom, setHasScrolledToBottom] = useState(false);

    useEffect(() => {
      const handleScroll = () => {
        // Высота документа (вся страница)
        const documentHeight = document.documentElement.scrollHeight;

        // Текущая позиция скролла
        const scrollPosition = window.scrollY + window.innerHeight;

        // Если позиция скролла достигает или превышает высоту страницы
        if (scrollPosition >= documentHeight) {
          setHasScrolledToBottom(true); // Пользователь долистал до низа
        } else {
          setHasScrolledToBottom(false);
        }
      };

      window.addEventListener("scroll", handleScroll);

      // Удаление слушателя при размонтировании компонента
      return () => {
        window.removeEventListener("scroll", handleScroll);
      };
    }, []);

    const [lastPost, SetLastPost] = useState("")

    let auth = useContext(AuthContext);

    const [feedData, SetFeedData] = useState([]);

    const [gotData, SetGotData] = useState("Pending");

    useEffect(
        () => {
            const fetchData = async () => {
                try{
                    let post_time_prefix = "";
                    if (feedData && feedData.length > 0)
                    {
                        post_time_prefix = "&last_post_time=" + feedData[feedData.length-1].created_at;
                    }
                    console.log(post_time_prefix)
                    const response = await axios.get(props.api_url + post_time_prefix)
                    SetFeedData(response.data);
                    SetGotData("Success");
                }
                catch (e)
                {
                  console.log(e);
                  SetGotData("Fail");
                }
              };

            setTimeout(fetchData, 1000);
            }
    , [hasScrolledToBottom])

    if (gotData == "Pending") {
        return (
            <>
                <div className="loading-block">
                    <h2> Loading data... </h2>
                </div>
            </>
        );
    }

    if (gotData == "Fail")
    {
        return (
            <>
                <div className="loading-block">
                    <h2> Failed to fetch data </h2>
                </div>
            </>
        );
    }


    return ( 
        <>
            <div className="feed-content"> 
                <div className="feed-box">
                    {
                        feedData.map((item) => (
                            <FeedPost item={item} key={item.id}></FeedPost>
                        ))
                    }
                </div>
            </div>
        </>
     );
}

export default FeedContent;
