import useLoad from "./UseLoad";
import FeedPost from "./FeedPost";
import "./styles/FeedContent.css"
import { AuthContext } from "./context/AuthProvider";
import { useContext, useState } from "react";

const FeedContent = (props) => {

    function getPostTime() {
        if (feedData && feedData.length != 0)
        {
            return "&last_post_time=" + feedData[feedData.length-1].created_at;
        }
        else
        {
            return "";
        }

    }

    let auth = useContext(AuthContext);

    console.log(auth?.username)
    const [feedData, SetFeedData] = useState([]);
    const [receivedData, SetReceivedData] = useState(false);

    console.log(props.api_url + getPostTime());

    const {data, receivedData: got_data} = useLoad(props.api_url + getPostTime());

    SetFeedData(data);
    SetReceivedData(got_data);


    if (receivedData == "Pending") {
        return (
            <>
                <div className="loading-block">
                    <h2> Loading data... </h2>
                </div>
            </>
        );
    }

    if (receivedData == "Fail")
    {
        return (
            <>
                <div className="loading-block">
                    <h2> Failed to fetch data </h2>
                </div>
            </>
        );
    }

    return ( 
        <>
            <div className="feed-content"> 
                <div className="feed-box">
                    {
                        feedData.map((item) => (
                            <FeedPost item={item} key={item.id}></FeedPost>
                        ))
                    }
                </div>
            </div>
        </>
     );
}

export default FeedContent;
