import { useState } from "react";
import "./styles/Registration.css"

const RegisterForm = (params) => {
    const [login, setLogin] = useState("");
    const [password, setPassword] = useState("");
    const [repeatPassword, setRepeatPassword] = useState("");
    const [authStatus, setAuthStatus] = useState("");
    const [email, setEmail] = useState("");
    const navigate = useNavigate();

    function Register(event) {
        const setRegister = async (e) =>  {
            e.preventDefault();
            console.log(params.api_url + "register");
            const response = await axios.post(params.api_url + "register", {
                "username": login,
                "email": email,
                "password": password
              })
            if (response?.data["successful"])
            {
                setAuthStatus("Аккаунт создан, перенаправление на страницу входа");
                navigate("/login")
            }
            else
            {
                console.log(response?.data["failStatus"])
                if (response?.data["failStatus"])
                {
                    setAuthStatus("");
                }
                else
                {
                    setAuthStatus("Не удалось создать аккаунт, попробуйте позже");
                }
            }
            setLogin("");
            setPassword("");
        }

        event.preventDefault();
        if (password != repeatPassword)
        {
            setAuthStatus("Введенные пароли не совпадают!");
        }
        else
        {

        }
    }

    return ( 
        <>
            <form className="register-form">
                <label> Email: </label>
                <input type="email" id="registerLoginInput" placeholder="введите электронную почту" onChange={(e) => {setLogin(e.target.value)}} value={login} required/>
                <label> Логин: </label>
                <input type="text" id="registerLoginInput" placeholder="введите логин" onChange={(e) => {setLogin(e.target.value)}} value={login} required/>
                <label> Пароль: </label>
                <input type="password" id="registePasswordInput" placeholder="введите ваш пароль" onChange={(e) => {setPassword(e.target.value)}} value={password} required></input>
                <label> Повторите пароль: </label>
                <input type="password" id="registePasswordInput" placeholder="введите пароль повторно" onChange={(e) => {setRepeatPassword(e.target.value)}} value={repeatPassword} required></input>
                <button>Регистрация</button>
                <div>{authStatus}</div>
            </form>
        </>
     );
}

export default RegisterForm;
