import { AuthContext } from "./context/AuthProvider";
import { useContext, useState, useEffect } from "react";
import useLoad from "./UseLoad";
import { useParams } from "react-router-dom";
import "./styles/PostDetail.css"
import axios from "axios";


// class post_serializer(serializers.ModelSerializer):
//     class Meta:
//         model = Post
//         fields = ["id", "author", "postname", "content", "created_at"]

const PostComponent = (props) => {
    const {auth, setAuth} = useContext(AuthContext);

    const { post_id } = useParams();

    const [ data, setData ] = useState(null);
    const [ fetchStatus, setFetchStatus ] = useState("pending");


    useEffect(() => {
        const fetchData = async () => {
            try {
                const response = await axios.get(`${props.fetch_post_url}?request_id=${post_id}`);
                setData(response.data);
                setFetchStatus("success");
                console.log(props.images_path + response.data["images"][0]["file"]);
            } catch (e) {
                console.error(e);
                setFetchStatus("fail");
            }
        };

        fetchData();
    }, [post_id]);

    if (fetchStatus == "success")
    {
        return ( 
            <>
                <h1 className="post-name">{data["post_info"]["postname"]}</h1>
                <div className="post-content">{data["post_info"]["content"]}</div>
                <div className="post-images-box">
                    {
                        data["images"].map((item) => {
                            <img className="post-image" src={ props.images_path + item["file"] }></img>
                        })
                    }
                </div>
            </>
        );
    }
    else if (fetchStatus == "pending") {
        return (
            <>
                <div> Fetching data... </div>
            </>
        )
    }
    else {
        return (
            <>
                <div> Oops, something went wrong </div>
            </>
        )
    }
}

export default PostComponent;
