import FeedPost from "./FeedPost";
import "./styles/FeedContent.css";
import { AuthContext } from "./context/AuthProvider";
import { useContext, useEffect, useState } from "react";
import axios from "axios";
import { v4 as uuidv4 } from "uuid";
import { ScrollContext } from "./context/ScrollProvider";
import { useLocation } from "react-router-dom";

const FeedContent = (props) => {
  const { scroll, setScroll } = useContext(ScrollContext);
  const [postsFetchAmount, SetPostsFetchAmount] = useState(20);
  const [hasScrolledToBottom, setHasScrolledToBottom] = useState(false);

  const [feedData, SetFeedData] = useState([]);
  const [gotData, SetGotData] = useState("Pending");

  // Сохранение позиции скролла перед уходом со страницы
  useEffect(() => {
    const handleBeforeUnload = () => {
      // Сохраняем текущую позицию скролла в состояние контекста
      setScroll(window.scrollY);
    };

    window.addEventListener("beforeunload", handleBeforeUnload);

    return () => {
      window.removeEventListener("beforeunload", handleBeforeUnload);
    };
  }, [setScroll]);

  // Восстановление позиции скролла при возврате на страницу
  useEffect(() => {
    if (scroll) {
      window.scrollTo(0, scroll); // Прокрутка к сохраненной позиции
      setScroll(null); // Сбрасываем сохраненную позицию
    }
  }, [scroll, setScroll]);

  useEffect(() => {
    const handleScroll = () => {
      // Высота документа (вся страница)
      const documentHeight = document.documentElement.scrollHeight;

      // Текущая позиция скролла
      const scrollPosition = window.scrollY + window.innerHeight;

      // Если позиция скролла достигает или превышает высоту страницы
      if (scrollPosition >= documentHeight) {
        setHasScrolledToBottom(true); // Пользователь долистал до низа
      } else {
        setHasScrolledToBottom(false);
      }
    };

    window.addEventListener("scroll", handleScroll);

    // Удаление слушателя при размонтировании компонента
    return () => {
      window.removeEventListener("scroll", handleScroll);
    };
  }, []);

  let auth = useContext(AuthContext);

  useEffect(() => {
    const fetchData = async () => {
      try {
        let post_time_prefix = "";
        if (feedData && feedData.length > 0) {
          let last_posts = [];
          feedData.slice(-postsFetchAmount).map((item) => {
            last_posts.push(item.id);
          });
          console.log(last_posts);
          post_time_prefix = "&last_posts=" + last_posts;
        }
        console.log(
          props.api_url + "posts/?amount=" + postsFetchAmount + post_time_prefix
        );
        const response = await axios.get(
          props.api_url + "posts/?amount=" + postsFetchAmount + post_time_prefix
        );

        SetFeedData(feedData.concat(response.data));
        SetGotData("Success");
      } catch (e) {
        console.log(e);
        SetGotData("Fail");
      }
    };

    setTimeout(fetchData, 1000);
  }, [hasScrolledToBottom]);

  if (gotData === "Pending") {
    return (
      <>
        <div className="loading-block">
          <h2> Loading data... </h2>
        </div>
      </>
    );
  }

  if (gotData === "Fail") {
    return (
      <>
        <div className="loading-block">
          <h2> Failed to fetch data </h2>
        </div>
      </>
    );
  }

  return (
    <>
      <div className="feed-content">
        <div className="feed-box">
          {feedData.map((item) => (
            <FeedPost item={item} key={uuidv4()} />
          ))}
        </div>
      </div>
    </>
  );
};

export default FeedContent;
